import React from 'react';
import styled from 'styled-components';
import SectionHeading from './styled/SectionHeading.styled';

const Footer = () => {
  return (
    <StyledFooter>
      <SectionHeading noBg>Contact</SectionHeading>
    </StyledFooter>
  );
};

export default Footer;

const StyledFooter = styled.footer`
  background: rgb(255, 132, 24);
  background: radial-gradient(
    circle,
    rgba(255, 132, 24, 1) 0%,
    rgba(128, 136, 255, 1) 100%
  );
  width: 100%;
  height: 250px;
  text-align: center;

  & h4 {
    background-color: ${(props) => (props.noBg ? 'none' : 'var(--sub-color)')} 
    margin: 1.1rem auto 0.5rem;
  } 

  & a {
    color: white;
    text-decoration: none;
  }
`;
