import styled from 'styled-components';

const SectionMainHeading = styled.h3`
  background-color: var(--main-color);
  color: white;
  font-size: 1.5rem;
  font-weight: 700;
  letter-spacing: 1px;
  padding: 0.2rem 0.4rem;
  margin: 0 auto 2rem;
  width: fit-content;
  text-transform: uppercase;
`

export default SectionMainHeading;
