import styled from 'styled-components';

const TopicsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 0.3rem;
  height: fit-content;
  margin: 0.5rem 0;

    & span {
      font-family: 'Arial', sans-serif;
      font-size: 1rem;
      letter-spacing: .2rem;
      color: var(--main-color);
    }

    & a span {
      transition: all 0.3s;
    }
    & a:hover a span {
      transform: translateX(0.3rem);
    }
`;

export default TopicsContainer;
