import styled from 'styled-components';

const Topic = styled.p`
  border: 1px solid black;
  color: black;
  font-family: var(--sub-font);
  font-size: 0.8rem;
  padding: 0.15rem 0.25rem;
  text-transform: uppercase;
  word-break: keep-all;
`;

export default Topic;
