import styled from 'styled-components';

const TopicLabel = styled.p`
  background-color: var(--bg-color);
  color: black;
  border: 1px solid black;
  font-family: var(--sub-font);
  font-size: 0.8rem;
  padding: 0.2rem;
  height: fit-content;
  text-transform: uppercase;
`;

export default TopicLabel;
