import styled from 'styled-components';

const SkillsGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  text-align: center;
  align-items: center;
  justify-items: center;
  margin: var(--margin);
  width: var(--width);

  & ul {
    font-size: 1rem;
    margin-top: 5px;
    line-height: var(--line-height);

  }
`;

export default SkillsGrid;
