import styled from 'styled-components';
import project1 from '../../assets/project_happy_thoughts.png'
import project2 from '../../assets/project_movies.png';
import project3 from '../../assets/project_survey.png';
import project4 from '../../assets/project_weather_app.png';
import profile from '../../assets/profile_pic.jpeg';

const imageProps = [project1, project2, project3, project4];

const ProjectImage = styled.div`
  background-image: ${imageProps.forEach((item) => (props) => item) ? `url(${item.slice(5)})` : null};
  background-size: cover;
  aspect-ratio: 1.5 / 1;
  min-width: 100%;
`

export default ProjectImage;
