/* eslint-disable no-nested-ternary */
import styled from 'styled-components';
import projectImg1 from '../../assets/happy_thoughts.png'
import projectImg2 from '../../assets/chatbot.png';
import projectImg3 from '../../assets/survey.png';
import projectImg4 from '../../assets/weather_app.png';

const ProjectImg = styled.div`
  background-image: ${(props) => (
    props.project1 ? `url(${projectImg1})`
      : props.project2 ? `url(${projectImg2})`
        : props.project3 ? `url(${projectImg3})`
          : props.project4 ? `url(${projectImg4})`
            : null)};
  background-size: cover;
  aspect-ratio: 1.5 / 1;
  border: 1px solid var(--third-color);
  box-shadow: 3px 3px var(--third-color);

    & div {
      background-color: var(--main-color);
      background-color: #e5e5f7;
opacity: 0.8;
background: radial-gradient(circle, transparent 20%, #e5e5f7 20%, #e5e5f7 80%, transparent 80%, transparent), radial-gradient(circle, transparent 20%, #e5e5f7 20%, #e5e5f7 80%, transparent 80%, transparent) 20px 20px, linear-gradient(#444cf7 1.6px, transparent 1.6px) 0 -0.8px, linear-gradient(90deg, #444cf7 1.6px, #e5e5f7 1.6px) -0.8px 0;
background-size: 40px 40px, 40px 40px, 20px 20px, 20px 20px;

      height: 100%;
      margin: 0;
      transition: all 0.3s;
      text-align: center;
    }

    &:hover div {
      opacity: 0;
    }
`

export default ProjectImg;
