import styled from 'styled-components';
// import project1 from '../../assets/project_happy_thoughts.png'
import project2 from '../../assets/project_movies.png';
import project3 from '../../assets/project_survey.png';
// import project4 from '../assets/project_weather_app.png';

const ProjectImage = styled.div`
  background-image: ${(props) => (props.project ? `url(${project2})` : `url(${project3})`)};
  background-size: cover;
  aspect-ratio: 1.5 / 1;
  min-width: 100%;
`

export default ProjectImage;
