import styled from 'styled-components';

const ArticleCard = styled.article`
  display: grid;
  grid: 1fr;
  ${({ other }) => (other && { margin: 'var(--margin)', width: 'var(--width)' })};

    & h5 {
      color: red;
    }
`;

export default ArticleCard;
