import styled from 'styled-components';

const SectionHeading = styled.h3`
  background-color: ${(props) => (props.noBg ? 'none' : 'var(--main-color)')};
  color: white;
  font-size: 1.5rem;
  font-weight: 700;
  letter-spacing: 1px;
  padding: 0.2rem 0.4rem;
  margin: ${(props) => (props.margin ? '1.1rem auto 0.5rem' : '0 auto 2rem')};
  width: fit-content;
  text-transform: uppercase;
`

export default SectionHeading;
